import { useLoaderData } from "react-router-dom";
import TaskList from "../components/TaskList";

const Tasks = () => {
    const data = useLoaderData();
    if (data.error) {
        // Check for specific error conditions and provide custom error messages
        let errorMessage = 'An error occurred';
        if (data.error instanceof Error) {
            errorMessage = data.error.message;
        } else if (data.error.status === 404) {
            errorMessage = 'Task not found';
        } else if (data.error.status === 500) {
            errorMessage = 'Server error';
        }

        return (
            <div class="alert alert-danger" role="alert">
                {errorMessage}
            </div>
        );
    }
    const tasks = data.events
    return (<>
        <TaskList tasks={tasks} />

    </>)

}

export default Tasks;

export async function loader() {
    try{
        const response = await fetch('http://localhost:5000/e');
        if(response.ok)
        {
            return response;
        }
    }
    catch (error) {
        throw new Error('Failed to fetch data'); // Set an appropriate error message
      }
   
}
