const Auth = () =>{
    return(
        <AuthForm/>
    )
}
export default Auth;

export async function action({request} ){
     const serchParams = new URL( request.url).searchParams;
     const mode = serchParams.get(mode) || 'login'

    const data = await request.formData();
    const userData = {
       email:data.get('email'),
       password:data.get('password')
   }
   console.log(userData)
   console.log(mode);

  const resposnse = await fetch(`http://localhost:5000/user/${mode}`, {
   method:'POST',
   body:JSON.stringify(userData),
   headers:{
    "Content-Type": "application/json"
   }
  });

  if(!resposnse.ok)
  {
    console.log(resposnse.error);
    console.log('somthing went wrong')
  }
  
    console.log("success ",resposnse)
    return redirect('/')
  
 

}
