import { Form, json, useNavigation, useSearchParams,Link } from "react-router-dom";
import classes from './AuthForm.module.css'
const AuthForm = () => {

    const navigation = useNavigation()
    const [serchParams] = useSearchParams();
    const isLoging = serchParams.get('mode') === 'loging';

    const isSubmitting = navigation.state === 'submitting';

    return (
        <Form className={classes.form} method="post">
                  <h1>{isLoging? 'Login'  : 'Create new user'}</h1>
            <p>
                <label htmlFor="email" >Email</label>
                <input type="text" name="email" id="email" />
            </p>
            <p>
                <label htmlFor="password" >password</label>
                <input type="password" name="password" id="email" />
            </p>
            <div className={classes.actions}>
                <Link to={`?mode=${isLoging? 'signup' : 'login'}`}>
                {isLoging? 'Login'  : 'Create new user'}
                </Link>
                <button type='submit'>{isSubmitting? 'Submitting...' : 'save'}</button>
                
            </div>

        </Form>
    )
}

export default AuthForm;
